INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(6744, 660, 'box_1_text', 'Das Schreiner-Handwerk befasst sich längst nicht mehr "nur" mit Holz. Zahlreiche Materialien werden vom Hüsser-Team verarbeitet und gibt so Ihrer Küche eine individuelle Note mit eigenem Charakter. Mit viel Respekt vor den Launen der Natur und genau nach Ihren Bedürfnissen werden die verschiedensten Materialien zusammengeführt und verarbeitet.'),
(6745, 660, '_box_1_text', 'field_55aa70c620d0b'),
(6746, 660, 'big_image', '170'),
(6747, 660, '_big_image', 'field_55aa70e3b438c'),
(6748, 660, 'box_2_title', 'Materialien - von draussen zu uns'),
(6749, 660, '_box_2_title', 'field_55aa70f5b438d'),
(6750, 660, 'box_2_subtitle', 'Sehen, spüren, fühlen und riechen Sie die Materialien.'),
(6751, 660, '_box_2_subtitle', 'field_55aa714702378'),
(6752, 660, 'box_2_text', 'Wir möchten, dass Sie die Materialien bei uns mit allen Sinnen erfahren können. Besuchen Sie unsere Ausstellung und Ihre Sinne werden durch das Erkunden von Holz, Metall, Glas, Kunstharz und vielen weiteren Materialien gefordert und befriedigt.'),
(6753, 660, '_box_2_text', 'field_55aa7136b438e'),
(6754, 660, 'box_3_image', '455'),
(6755, 660, '_box_3_image', 'field_55aa719f5dc72'),
(6756, 660, 'box_3_title', 'Alexandra Lüthi'),
(6757, 660, '_box_3_title', 'field_55aa71965dc71'),
(6758, 660, 'box_3_contact', 'Lernende 2. Lehrjahr '),
